//WAP to sum the value of an array   {67,4,65,98,34}
//WAP to get sum of first 10 natural number
package com.oopexample;

public class SumOfNaturalno {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int i,sum=0;
    for(i=1;i<=10;i++)
    {
    	sum=sum+i;
    	
    }
  System.out.println("The addition of first 10 natural number is:"+ sum);
    
	}

}
